import React, { useState } from 'react';
import axios from 'axios';

function VitalsForm() {
	const [formData, setFormData] = useState({
		heartRate: '',
		bloodPressure: '',
		respiratoryRate: '',
		bodyTemperature: '',
		symptoms: '',
	});

	const [message, setMessage] = useState('');

	// Handle Input Change
	const handleChange = (e) => {
		const { name, value } = e.target;
		setFormData({ ...formData, [name]: value });
	};

	// Submit Form
	const handleSubmit = async (e) => {
		e.preventDefault();

		try {
			// Send Data to Backend
			const response = await axios.post('http://localhost:5001/api/vitals', formData);
			setMessage(response.data.message);
			setFormData({
				heartRate: '',
				bloodPressure: '',
				respiratoryRate: '',
				bodyTemperature: '',
				symptoms: '',
			});
		} catch (error) {
			console.error('Error:', error);
			setMessage('Failed to submit vitals. Try again later.');
		}
	};

	return (
		<form onSubmit={handleSubmit}>
			<h2>Enter Your Vitals</h2>
			{message && <p>{message}</p>}

			<label>Heart Rate (bpm)</label>
			<input
				type="number"
				name="heartRate"
				placeholder="e.g., 75"
				value={formData.heartRate}
				onChange={handleChange}
			/>

			<label>Blood Pressure (mmHg)</label>
			<input
				type="text"
				name="bloodPressure"
				placeholder="e.g., 120/80"
				value={formData.bloodPressure}
				onChange={handleChange}
			/>

			<label>Respiratory Rate (breaths per min)</label>
			<input
				type="number"
				name="respiratoryRate"
				placeholder="e.g., 16"
				value={formData.respiratoryRate}
				onChange={handleChange}
			/>

			<label>Body Temperature (°F/°C)</label>
			<input
				type="number"
				name="bodyTemperature"
				placeholder="e.g., 98.6"
				value={formData.bodyTemperature}
				onChange={handleChange}
			/>

			<label>Symptoms</label>
			<textarea
				name="symptoms"
				placeholder="Describe any symptoms"
				value={formData.symptoms}
				onChange={handleChange}
			/>

			<button type="submit">Submit</button>
		</form>
	);
}

export default VitalsForm;
